import {useEffect, useState} from "react";
import axios from "axios";
import {Button, Image, SafeAreaView, ScrollView, Text, View,StyleSheet} from "react-native";

function Item({navigation,route}) {

    const {itemId, memberId} =route.params;
    const [item, setItem] = useState(null);
    const url = "http://localhost:8080/items/" + itemId ;
    const [like, setLike] = useState(false);

    useEffect(()=>{
        const getResponse = async () =>{
            const response = await axios.get(url);
            setItem(response.data);
        };
        getResponse();
    },[]);

    if(item == null){
        return <Text>로딩중</Text>
    }

    return (

        <View style={{
            flex:1,
            marginVertical:20
        }}>

            <View style ={{borderBottomWidth : 1,
                flex:1
            }}>
                <View style={{marginLeft : 14,
                    marginVertical:10 }}>
                    <Text style = {{fontSize : 20}}>{item.sellerName}</Text>
                    <Text>{item.gu}구 {item.dong}</Text>
                </View>
            </View>

            <View style={{flex:10}}>
                <Text style={{
                    marginLeft:15,
                    fontSize : 30,
                    marginVertical : 10
                }}>{item.itemName}</Text>

                <Text style={{
                    marginLeft:15,
                    marginVertical:10
                }}>등록일 {item.createDate}
                </Text>

                <Text style={{
                    marginLeft:15
                }}>{item.content}</Text>
            </View>

            <View style={{position:"absolute",
                left:0,
                right:0,
                bottom:20,
                height:40,
                backgroundColor:"yellow",
                alignItems:"center"
            }}>
                {like?(
                    <Text>like</Text>
                ):(
                    <Text>not like</Text>
                )}

                <Button title="like button"
                        onPress={
                            () => {

                                setLike(!like);

                                const data = JSON.stringify({like,memberId});
                                console.log(data)
                                axios.put(
                                    url + "/like",
                                    data,
                                    {
                                        headers: {
                                            'Content-Type': 'application/json'
                                        }
                                    }
                                ).then(
                                    console.log("success")
                                ).catch(err => console.log(err));
                            }


                        }
                />

            </View>
        </View>

    );
}

function ItemV2({navigation,route}) {

    const {itemId, memberId} =route.params;
    const [item, setItem] = useState(null);
    const url = "http://localhost:8080/items/" + itemId ;
    const [like, setLike] = useState(false);


    useEffect(()=>{
        const getResponse = async () =>{
            const response = await axios.get(url);
            setItem(response.data);
        };
        getResponse();
    },[]);

    if(item == null){
        return <Text>로딩중</Text>
    }

    return (
        <SafeAreaView>
            <ScrollView>

                <View>
                    <Image style={styles.img} source={{uri:"http://localhost:8080/items/image/" + item.uri}}/>
                </View>

                <View style={styles.sellerSection}>
                    <Text style={styles.sellerName}>{item.sellerName}</Text>
                </View>
            </ScrollView>
        </SafeAreaView>


    );
}

const styles = StyleSheet.create({
    img :{
        width: 430,
        height: 430,
    },
    sellerSection:{
        padding:40
    },
    sellerName:{
        fontSize:20
    }
})
export default ItemV2;
